"""Create a comma separated string from the strings provided with last comma trailed by 'and'."""
if len(list) == 0:
    return None
elif len(list) == 1:
    return str(list[0])
elif len(list) == 2:
    return ' and '.join([str(x) for x in list])
else:
    return ', '.join([str(x) for x in list[:-1]]) + ', and ' + str(list[-1])
