"""Check for safe dual or single-hemi input, returns list"""
if hemi is None:
    if self._hemi not in ['lh', 'rh']:
        hemi = ['lh', 'rh']
    else:
        hemi = [self._hemi]
elif hemi not in ['lh', 'rh']:
    extra = ' or None' if self._hemi in ['lh', 'rh'] else ''
    raise ValueError('hemi must be either "lh" or "rh"' + extra)
else:
    hemi = [hemi]
return hemi
