"""Checks the axis provided in numpy functions. Returns axis decremented by one and boolean if 0 is in original axis."""
if not axis is None:
    try:
        ax = np.asarray(axis)
    except TypeError:
        if axis == 0:
            returnAxis = None
            returnBool = True
        else:
            returnAxis = axis
            returnBool = False
    else:
        try:
            _ = len(ax)
        except TypeError:
            returnAxis = axis - 1
        else:
            returnAxis = tuple([(a - 1) for a in ax if a != 0])
        returnBool = 0 in ax
else:
    returnAxis = axis
    returnBool = False
return returnAxis, returnBool
