propname = delim + key
prefix = 'root'
datatype = ''
arr = obj[key]
if 'object' in categorize_by:
    prefix = obj._isa()
if 'element' in categorize_by:
    propname = delim + key.replace('.', delim)
if 'data' in categorize_by:
    if arr.dtype == bool:
        datatype = delim + 'labels'
    else:
        datatype = delim + 'properties'
path = prefix + delim + obj.name + datatype + propname
return path
