"""This can only be called when running python 2.7."""
if sys.version_info[0] != 2:
    pytest.fail('Called unicode checker for python 2.7 ' +
        'using another python version.')
if isinstance(anything, unicode):
    return True
elif isinstance(anything, str):
    return False
elif isinstance(anything, list):
    okay = True
    for e in anything:
        okay = okay and self.check_is_unicode(e)
        if not okay:
            return False
    return okay
elif isinstance(anything, dict):
    okay = True
    for key in anything:
        okay = okay and self.check_is_unicode(key) and self.check_is_unicode(
            anything[key])
        if not okay:
            return False
    return okay
return True
