import MJOLNIR.Statistics.CutObject
@functools.wraps(func)
def newFunction(*args, **kwargs):
    if 'internal' in kwargs:
        if kwargs['internal'] is True:
            del kwargs['internal']
            return func(*args, **kwargs)
    fittingKwargs = ['fitFunction', 'p0', 'costFunction', 'lock']
    temp = {}
    for fitKwarg in fittingKwargs:
        if fitKwarg in kwargs:
            temp[fitKwarg] = kwargs[fitKwarg]
            del kwargs[fitKwarg]
        else:
            temp[fitKwarg] = None
    ds = args[0]
    if len(args) > 1:
        otherArgs = args[1:]
    else:
        otherArgs = None
    co = MJOLNIR.Statistics.CutObject.CutObject(*func(*args, **kwargs),
        dataSet=ds, cutFunction=func, kwargs=kwargs, args=otherArgs)
    for fitKwarg in fittingKwargs:
        setattr(co, fitKwarg, temp[fitKwarg])
    return co
return newFunction
