outputs = {process.STDOUT: [], process.STDERR: []}
open = len(outputs)
def send(msg: process.StreamUpdate):
    nonlocal outputs, open
    outputs[msg.fileno].append(msg.chunk)
    if msg.chunk == b'':
        open -= 1
    if open > 0:
        return None
    return {fileno: b''.join(chunks) for fileno, chunks in outputs.items()}
return send
