"""Creates a random int32 tensor of the shape within the vocab size."""
if rng is None:
    rng = random.Random()
total_dims = 1
for dim in shape:
    total_dims *= dim
values = []
for _ in range(total_dims):
    values.append(rng.randint(0, vocab_size - 1))
return tf.constant(value=values, dtype=tf.int32, shape=shape, name=name)
