if isinstance(object, list):
    isListOfStr = True
    for item in object:
        if not isinstance(item, str):
            isListOfStr = False
            break
    if isListOfStr:
        return object
    else:
        raise AttributeError(
            'Data files provided are not a list of strings or string!')
elif isinstance(object, str):
    return [object]
else:
    raise AttributeError(
        'Data files provided are not a list of strings or string!')
