def send_response():
    sent_event.wait()
    sent_event.clear()
    if prompt:
        client.received_message(json.dumps({'id': '1', 'payload': {
            'pairingType': 'PROMPT'}}))
    if registered:
        client.received_message(json.dumps({'id': '1', 'payload': {
            'client-key': 'xyz'}, 'type': 'registered'}))
