class _Handler(SimpleHTTPRequestHandler):

    def do_POST(self):
        request_queue.put(dict(method=self.command, path=self.path, headers
            ={k.lower(): v for k, v in self.headers.items()}))
        self.send_response(201)
        self.end_headers()
port = 8000
while True:
    try:
        server = HTTPServer(('127.0.0.1', port), _Handler)
        break
    except OSError as e:
        if e.errno != 98:
            raise
        port += 1
return server
