"""Model with replicates."""
measurement_df = pd.DataFrame(data={OBSERVABLE_ID: ['obs_a', 'obs_a'],
    SIMULATION_CONDITION_ID: ['c0', 'c0'], TIME: [10, 10], MEASUREMENT: [0, 1]}
    )
observable_df = pd.DataFrame(data={OBSERVABLE_ID: ['obs_a'],
    OBSERVABLE_FORMULA: ['A'], NOISE_FORMULA: [2]}).set_index([OBSERVABLE_ID])
parameter_df = pd.DataFrame(data={PARAMETER_ID: ['par1', 'par2'],
    NOMINAL_VALUE: [3, 4]}).set_index([PARAMETER_ID])
simulation_df = measurement_df.copy(deep=True).rename(columns={MEASUREMENT:
    SIMULATION})
simulation_df[SIMULATION] = [2, 2]
expected_residuals = {(2 - 0) / 2, (2 - 1) / 2}
expected_residuals_nonorm = {2 - 0, 2 - 1}
expected_llh = -0.5 * (np.array(list(expected_residuals)) ** 2).sum(
    ) - 0.5 * np.log(2 * np.pi * np.array([2, 2]) ** 2).sum()
return measurement_df, observable_df, parameter_df, simulation_df, expected_residuals, expected_residuals_nonorm, expected_llh
