global response
global response_ptr
if nbytes > 0:
    ret = response[response_ptr:response_ptr + nbytes]
    response_ptr += nbytes
elif nbytes == 0:
    lines = response.splitlines()
    if len(lines) > response_ptr:
        ret = lines[response_ptr]
        response_ptr += 1
        return ret
    else:
        return None
elif nbytes == -1:
    return response
return ret
