            data = []
            try:
                len(shape[1])
            except:
                for d in self:
                    data.append(d.reshape(shape[1:]))
            else:
                for d, s in zip(self._data, shape[1]):
                    data.append(d.reshape(s))
            self.data = data
        else:
            raise AttributeError(
                'Shape provided ({}) does not fit Marray of length {}'.
                format(shape, len(self)))
else:
