self.dir_handler_cmd = Actions()
self.dir_handler_cmd.add(name='CreateTmpDir', pre_conditions={
    'tmp_dir_state': False, 'tmp_dir_content': False}, effects={
    'tmp_dir_state': True, 'tmp_dir_content': False}, shell=
    'mkdir -p /tmp/goap_tmp')
self.dir_handler_cmd.add(name='CreateToken', pre_conditions={
    'tmp_dir_state': True, 'tmp_dir_content': False}, effects={
    'tmp_dir_state': True, 'tmp_dir_content': True}, shell=
    'touch /tmp/goap_tmp/.token')
