@self.app.route('/')
def index():
    sorted_fields = request.args.getlist('sort')
    return render_template_string(
        """
                {% from "_tablehelpers.html" import render_header_link %}
                {{
                render_header_link('name', 'Name', sorted_fields)
                }}
                """
        , sorted_fields=sorted_fields)
