if not command:
    return None
env = dict(os.environ)
env['PYTHONPATH'] = self.testdir + os.pathsep + env['PYTHONPATH']
env['HOME'] = self.test_homedir
env.update(kwargs.pop('env', {}))
outputs = []
for cmd in command:
    try:
        outputs.append(check_output(shlex.split(cmd), env=env, cwd=self.
            testdir, **kwargs).decode('utf-8'))
    except CalledProcessError as e:
        if e.output:
            print(dedent(
                """                    ----------stdout from "{}"----------
                    {}
                    ----------{}----------
                    """
                ).format(cmd, e.output.decode('UTF-8'), 'end stdout'.center
                (14 + len(cmd))))
        if getattr(e, 'stderr', None):
            print(dedent(
                """                    ----------stderr from "{}"----------
                    {}
                    ----------{}----------
                    """
                ).format(cmd, e.stderr.decode('UTF-8'), 'end stderr'.center
                (14 + len(cmd))))
        raise
return outputs[0] if len(outputs) == 1 else outputs
