GenericObject = GeometryObject(position=(0, 1.0, 0.0), direction=(1.0, 0, 0))
try:
    GenericObject.direction = (0, 0), (0, 0)
    assert False
except AttributeError:
    assert True
try:
    GenericObject.direction = 0, 0, 0
    assert False
except AttributeError:
    assert True
try:
    GenericObject.direction = 0, 0, 0, 1
    assert False
except AttributeError:
    assert True
