data = {'a': [1, 2, 3], 'b': [1, 2, 3], 'c': [2, 1, 1], 'd': [1, 1, 1]}
features = pd.DataFrame(data)
table = {'dist_thresh = 0 -> 1 component': {'dist_thresh': 0, 'expected': [
    {'a', 'b'}]}, 'dist_thresh = 1 -> 2 components': {'dist_thresh': 1,
    'expected': [{'a', 'b'}, {'c', 'd'}]},
    'dist_thresh = 2 -> all connected': {'dist_thresh': 2, 'expected': [{
    'a', 'b', 'c', 'd'}]}, 'dist_thresh = -1 -> empty list': {'dist_thresh':
    -1, 'expected': []}}
for test_name, test in table.items():
    self.pruner._feature_group_thresh = test['dist_thresh']
    groups = self.pruner._group_features(features)
    self.assertListEqual(list(groups), test['expected'], test_name)
