resists = {'Elecromagnetic': 0, 'Thermal': 0.2, 'Kinetic': 0.4, 'Explosive':
    0.5}
damage_profile = {'Elecromagnetic': 0, 'Thermal': 0.5, 'Kinetic': 0.5,
    'Explosive': 0}
return_matrix = AdaptiveArmorHardener.run_cycle(resists, damage_profile)
assert return_matrix['AdaptivePattern']['Elecromagnetic'] == 9
assert return_matrix['AdaptivePattern']['Thermal'] == 21
assert return_matrix['AdaptivePattern']['Kinetic'] == 21
assert return_matrix['AdaptivePattern']['Explosive'] == 9
for _ in return_matrix['AppliedDamage']:
    key, value = _
    if key == 'Elecromagnetic':
        assert value == 0
    elif key == 'Thermal':
        assert value == 0.4
    elif key == 'Kinetic':
        assert value == 0.3
    elif key == 'Explosive':
        assert value == 0
    else:
        assert True is False
