resists = {'Elecromagnetic': 0, 'Thermal': 0.2, 'Kinetic': 0.4, 'Explosive':
    0.5}
damage_profile = {'Elecromagnetic': 0.5, 'Thermal': 0, 'Kinetic': 0,
    'Explosive': 0}
reactive_armor_hardener_profile = {'Elecromagnetic': 15, 'Thermal': 15,
    'Kinetic': 15, 'Explosive': 15}
return_matrix = None
for _ in range(5):
    if return_matrix:
        reactive_armor_hardener_profile = {'Elecromagnetic': return_matrix[
            'AdaptivePattern']['Elecromagnetic'], 'Thermal': return_matrix[
            'AdaptivePattern']['Thermal'], 'Kinetic': return_matrix[
            'AdaptivePattern']['Kinetic'], 'Explosive': return_matrix[
            'AdaptivePattern']['Explosive']}
    return_matrix = AdaptiveArmorHardener.run_cycle(resists, damage_profile,
        reactive_armor_hardener_profile)
assert return_matrix['AdaptivePattern']['Elecromagnetic'] == 57
assert return_matrix['AdaptivePattern']['Thermal'] == 3
assert return_matrix['AdaptivePattern']['Kinetic'] == 0
assert return_matrix['AdaptivePattern']['Explosive'] == 0
for _ in return_matrix['AppliedDamage']:
    key, value = _
    if key == 'Elecromagnetic':
        assert value == 0.5
    elif key == 'Thermal':
        assert value == 0
    elif key == 'Kinetic':
        assert value == 0
    elif key == 'Explosive':
        assert value == 0
    else:
        assert True is False
