"""Test BasisState with preparations on subsystems."""
if self.devices is None:
    return
self.logTestName()
for device in self.devices:
    for bits_to_flip in [np.array([0, 0, 0]), np.array([1, 0, 0]), np.array
        ([0, 1, 1]), np.array([1, 1, 0]), np.array([1, 1, 1])]:

        @qml.qnode(device)
        def circuit():
            qml.BasisState(bits_to_flip, wires=list(range(self.
                num_subsystems - 1)))
            return qml.expval(qml.PauliZ(0)), qml.expval(qml.PauliZ(1)
                ), qml.expval(qml.PauliZ(2)), qml.expval(qml.PauliZ(3))
        self.assertAllAlmostEqual([1] * (self.num_subsystems - 1) - 2 *
            bits_to_flip, np.array(circuit()[:-1]), delta=self.tol)
