facade = ChatetteFacade.get_or_create()
file_path = (
    'tests/system-testing/inputs/generate-nb/training-only/bugfixes/bug-22-slot-position.chatette'
    )
facade.run(file_path)
for ex in facade.train_examples:
    for entity in ex.entities:
        if entity._len != 5 and entity._len != 8:
            pytest.fail("Incorrect length for entity '" + str(entity.value) +
                "': " + str(entity._len))
        if entity._start_index < 26 and entity._start_index > 35:
            pytest.fail("Incorrect starting index for entity '" + str(
                entity.value) + "' in '" + str(ex.text) + "': " + str(
                entity._start_index))
