old = pydevd_reload.notify_error
self._called = False
def on_error(*args):
    self._called = True
try:
    pydevd_reload.notify_error = on_error
    pydevd_reload.Reload(None)._update(None, None, F, G)
    self.assertTrue(self._called)
finally:
    pydevd_reload.notify_error = old
