sino, angles = create_test_sino_3d(Nx=10, Ny=10)
p = get_test_parameter_set(1)[0]
f = odtbrain.backpropagate_3d(sino, angles, padval=0, dtype=np.float64,
    copy=False, **p)
try:
    odtbrain.apple.correct(f=f, res=p['res'], nm=p['nm'], enforce_envelope=1.05
        )
except ValueError:
    pass
else:
    assert False, '`enforce_envelope` must be in [0, 1]'
