"""Test conversion of RA to HMS strings"""
for dec, dstr in [(-15, '23:00:00.00'), (15, '01:00:00.00'), (23.5678,
    '01:34:16.27'), (np.nan, 'XX:XX:XX.XX'), (np.inf, 'XX:XX:XX.XX')]:
    ans = at.dec2hms(dec)
    if not ans == dstr:
        raise AssertionError('{0} != {1}'.format(ans, dstr))
