if self.devices is None:
    return
self.logTestName()
for device in self.devices:

    @qml.qnode(device)
    def circuit():
        qml.PauliX(wires=[0])
        qml.BasisState(np.array([0, 1, 0, 1]), wires=list(range(self.
            num_subsystems)))
        return qml.expval(qml.PauliZ(0))
    self.assertRaises(pennylane._device.DeviceError, circuit)
