sector_mapper = {'GOOG': 'tech', 'AAPL': 'tech', 'FB': 'tech', 'AMZN':
    'tech', 'BABA': 'tech', 'GE': 'utility', 'AMD': 'tech', 'WMT': 'retail',
    'BAC': 'fig', 'GM': 'auto', 'T': 'auto', 'UAA': 'airline', 'SHLD':
    'retail', 'XOM': 'energy', 'RRC': 'energy', 'BBY': 'retail', 'MA':
    'fig', 'PFE': 'pharma', 'JPM': 'fig', 'SBUX': 'retail'}
sector_upper = {'tech': 0.2, 'utility': 0.1, 'retail': 0.2, 'fig': 0.4,
    'airline': 0.05, 'energy': 0.2}
sector_lower = {'utility': 0.01, 'fig': 0.02, 'airline': 0.01}
ef = setup_efficient_frontier()
ef.add_sector_constraints(sector_mapper, sector_lower, sector_upper)
weights = ef.efficient_risk(0.19)
for sector in list(set().union(sector_upper, sector_lower)):
    sector_sum = 0
    for t, v in weights.items():
        if sector_mapper[t] == sector:
            sector_sum += v
    assert sector_sum <= sector_upper.get(sector, 1) + 1e-05
    assert sector_sum >= sector_lower.get(sector, 0) - 1e-05
