from cookie_eater.base import get_platform
try:
    FirefoxCookies()
except Exception:
    pass
_db_paths = {'windows': '~\\AppData\\Roaming\\Mozilla\\Firefox\\Profiles\\',
    'mac': '~/Library/Application Support/Firefox/Profiles/', 'linux':
    '~/.mozilla/firefox/'}
path = os.path.expanduser(_db_paths[get_platform()])
test_path = os.path.join(path, 'reusables_test.default')
try:
    os.mkdir(test_path)
except Exception:
    pass
try:
    FirefoxCookies()
except BrowserException as err:
    assert 'Cookie does not exist at' in str(err)
finally:
    shutil.rmtree(test_path, ignore_errors=True)
