"""Test 'gen_hdf5_quantitative_analysis_dataset'.
    Simple test, which checks if all the files are created"""
wd = os.path.join(tmp_path, 'file_dir')
standards_serials = ['41151', '41163']
test_elements = {}
test_elements['Fe'] = {'density': 50}
test_elements['W'] = {'density': 70}
test_elements['Au'] = {'density': 80}
files_saved = gen_hdf5_qa_dataset(wd=wd, standards_serials=
    standards_serials, test_elements=test_elements)
assert len(files_saved
    ) == 4, f'Incorrect number of saved files: expected 4, saved {len(files_saved)}'
for fln in files_saved:
    assert os.path.isfile(fln), f"File '{fln}' was not created"
