cron = doccron.cron('0 0 * * 7#2 *')
second_sunday = datetime.now(tz=tzlocal()).replace(hour=0, minute=0, second
    =0, microsecond=0)
while True:
    second_sunday += timedelta(days=1)
    if second_sunday.isoweekday() == 7:
        break
while True:
    if second_sunday.month == (second_sunday - timedelta(days=7)
        ).month and second_sunday.month != (second_sunday - timedelta(days=14)
        ).month:
        break
    second_sunday += timedelta(days=7)
assert next(cron) == second_sunday
