"""
        Poke at the hits returned by a search and see if document data and
        teaser text come out right.
        """
with self.freshenDatabase() as db:
    cc = HCondition(u'hi-res')
    for hit in db.search(cc):
        self.assertEqual(str(hit),
            """@digest=17de33c57e358f0fc5d57cd26a08b48e
@id=1
@uri=1

word this is my document. do you like documents? this one is hi-res.
"""
            )
        self.assertEqual(hit.teaser([u'document']),
            u'word this is my <strong>document</strong>. do you li ... ke <strong>document</strong>s? this one is hi-re ... '
            )
        self.assertEqual(hit.teaser([u'document'], 'rst'),
            u'word this is my **document**. do you li ... ke **document**s? this one is hi-re ... '
            )
        self.assertRaises(NotImplementedError, hit.teaser, [u'document'], 'pdf'
            )
