def try_shape(shape):
    try:
        insane.pbc.PBC(shape=shape, xyz=(10, 10, 10))
    except insane.pbc.PBCException as e:
        if not 'is not a known PBC shape' in str(e):
            raise AssertionError()
    else:
        raise AssertError()
    shapes = 'invalid', '', 9, True, False
    for shape in shapes:
        yield try_shape, shape
