outfil = 'tst.log'
msgs = pypmsgs.Messages(outfil, verbosity=1)
msgs.close()
with open(outfil, 'r') as f:
    lines = f.readlines()
pckgs = ['scipy', 'numpy', 'astropy']
flgs = [False] * len(pckgs)
for line in lines:
    for jj, pckg in enumerate(pckgs):
        if pckg in line:
            flgs[jj] = True
for flg in flgs:
    assert flg is True
