data = build_data()
model = build_model(data)
hmc_helper = gpflow.optimizers.SamplingHelper(model.log_posterior_density,
    model.trainable_parameters)
for i in range(len(model.trainable_parameters)):
    assert model.trainable_parameters[i].shape == hmc_helper.current_state[i
        ].shape
    assert model.trainable_parameters[i] == hmc_helper._parameters[i]
    assert model.trainable_parameters[i
        ].unconstrained_variable == hmc_helper.current_state[i]
