"""Construct a namespace from a dictionary compatable initialization."""
assert Namespace({'A': 1, 'B': 2, 'C': {'X': 'hello', 'Y': 'world'}}
    ) == Namespace([('A', 1), ('B', 2), ('C', Namespace([('X', 'hello'), (
    'Y', 'world')]))]) == Namespace(A=1, B=2, C=Namespace(X='hello', Y='world')
    )
