"""test %calls that are nested inside each other"""
t = Template(
    """
            <%def name="foo()">
                ${caller.body(x=10)}
            </%def>

            x is ${x}
            <%def name="bar()">
                bar: ${caller.body()}
            </%def>

            <%call expr="foo()" args="x">
                this is foo body: ${x}

                <%call expr="bar()">
                    this is bar body: ${x}
                </%call>
            </%call>
"""
    )
assert result_lines(t.render(x=5)) == ['x is 5', 'this is foo body: 10',
    'bar:', 'this is bar body: 10']
