template = Template(
    """
            <%!
                def bat(fn):
                    def decorate(context):
                        return "function " + fn.__name__ + " " + runtime.capture(context, fn)
                    return decorate
            %>

            <%def name="foo()">

                <%def name="bar()" decorator="bat">
                    this is bar
                </%def>
                ${bar()}
            </%def>

            ${foo()}
        """
    )
assert flatten_result(template.render()) == 'function bar this is bar'
