"""test foo:bar syntax, including multiline args and expression eval."""
t = Template(
    """
            <%def name="foo(x, y, q, z)">
                ${x}
                ${y}
                ${q}
                ${",".join("%s->%s" % (a, b) for a, b in z)}
            </%def>

            <%self:foo x="this is x" y="${'some ' + 'y'}" q="
                this
                is
                q"

                z="${[
                (1, 2),
                (3, 4),
                (5, 6)
            ]

            }"/>
        """
    )
eq_(result_lines(t.render()), ['this is x', 'some y', 'this', 'is', 'q',
    '1->2,3->4,5->6'])
