class Params1(Parameters):
    array_first = False
    defaults = {'schema': {'labels': {'mylabel': {'type': 'int',
        'validators': {'range': {'min': 0, 'max': 10}}}, 'somelabel': {
        'type': 'int', 'validators': {'range': {'min': 0, 'max': 10}}}},
        'operators': {'array_first': False, 'label_to_extend': 'somelabel'}}}
params = Params1(array_first=True, label_to_extend='mylabel')
assert params.array_first
assert params.label_to_extend == 'mylabel'
assert params.operators == {'array_first': True, 'label_to_extend':
    'mylabel', 'uses_extend_func': False}
Params1.array_first = True
params = Params1()
assert params.array_first
assert params.label_to_extend == 'somelabel'
assert params.operators == {'array_first': True, 'label_to_extend':
    'somelabel', 'uses_extend_func': False}
class Params2(Parameters):
    defaults = {'schema': {'operators': {'array_first': True}}}
params = Params2()
assert params.array_first
assert params.label_to_extend is None
assert params.operators == {'array_first': True, 'label_to_extend': None,
    'uses_extend_func': False}
class Params3(Parameters):
    array_first = True
    label_to_extend = 'hello'
    defaults = {'schema': {'operators': {'array_first': True}}}
params = Params3(array_first=False, label_to_extend=None)
assert params.operators == {'array_first': False, 'label_to_extend': None,
    'uses_extend_func': False}
