t = Template(
    """
        <%def name="a()">
            a: x is ${x}
        </%def>

        <%def name="b()">
            <%def name="c()">
            <%
                x = 10
            %>
            c. x is ${x}.  ${a()}
            </%def>

            b. ${c()}
        </%def>

        ${b()}

        x is ${x}
"""
    )
eq_(flatten_result(t.render(x=5)), 'b. c. x is 10. a: x is 5 x is 5')
