for w in range(3, 11):
    test_config = self.test_conf_const.__class__
    data, config = crowdtruth.load(file=TEST_FILE_PREF + str(w) +
        'work_outlier.csv', config=test_config())
    results = crowdtruth.run(data, config)
    self.assertAlmostEqual(results['workers']['wqs'].at['W1'], 0.0)
    self.assertAlmostEqual(results['units']['uqs'].at[1], 1.0)
    for x in range(1, w):
        self.assertAlmostEqual(results['workers']['wqs'].at['W' + str(x + 1
            )], 1.0)
    if not config.open_ended_task:
        self.assertAlmostEqual(results['annotations']['aqs'].at['A'], 0.0)
        self.assertAlmostEqual(results['annotations']['aqs'].at['B'], 1.0)
