collection = lookup.TemplateLookup()
collection.put_string('base',
    """
            this is the base.

            ${next.body(**context.kwargs)}

            <%def name="foo(**kwargs)">
                ${next.body(**kwargs)}
            </%def>

            <%def name="bar(**otherargs)">
                ${next.body(z=16, **context.kwargs)}
            </%def>

            ${foo(x=12, y=15, z=8)}
            ${bar(x=19, y=17)}
        """
    )
collection.put_string('index',
    """
            <%inherit file="base"/>
            <%page args="x, y, z=7"/>
            pageargs: ${x}, ${y}, ${z}
        """
    )
assert result_lines(collection.get_template('index').render(x=5, y=10)) == [
    'this is the base.', 'pageargs: 5, 10, 7', 'pageargs: 12, 15, 8',
    'pageargs: 5, 10, 16']
