data = parse_compound_formula(formula)
assert len(data) == len(elements), 'The number of parsed elements is incorrect'
assert set(elements) == set(data.keys()
    ), 'The set of parsed elements is incorrect'
assert tuple(data[e]['nAtoms'] for e in elements
    ) == n_atoms, 'The number of atoms in parsed data is determined incorrectly'
pytest.mark.parametrize('formula, elements, n_atoms', [('Fe2O3', ('Fe', 'O'
    ), (2, 3)), ('He', ('He',), (1,)), ('H2SO4', ('H', 'S', 'O'), (2, 1, 4)
    ), ('C', ('C',), (1,)), ('C2H5OH', ('C', 'H', 'O'), (2, 6, 1))])
