class ExampleOne(Requirement):

    def handle(self, context: AuthorizationContext):
        pass
class ExampleTwo(Requirement):

    def handle(self, context: AuthorizationContext):
        pass
auth = get_strategy([Policy('user', ExampleOne(), ExampleTwo())])
@auth(policy='user')
def some_method():
    return True
with raises(UnauthorizedError, match=
    'The user is not authorized to perform the selected action. Failed requirements: ExampleOne, ExampleTwo.'
    ):
    some_method()
