class FakeRequest:

    @property
    def http_method(self):
        return 'fake http method'

    @property
    def request_uri(self):
        return 'fake request uri'
def fake_handler(thrown_exc, request):
    return ResponseBuilder().set_status(HTTPStatus.OK).build()
exception_handlers = ErrorHandlersBuilder().add_pre_response_error_handler(
    RouteNotFoundException, fake_handler).build()
response = exception_handlers.handle_pre_response_error(RouteNotFoundException
    (), FakeRequest())
self.assertEqual(HTTPStatus.OK.value, response.status.code)
