img = np.zeros((30, 40))
with mock.patch.object(cv2, 'VideoCapture', new_callable=mock.MagicMock
    ) as mock_cv_capture, mock.patch.object(NpCam, 'isOpened'
    ) as mock_is_open, mock.patch.object(subd, 'register_cam') as mock_reg:
    cap = NpCam(img)
    mock_cv_capture.side_effect = [cap]
    mock_is_open.return_value = False
    subd.CV_CAMS_DICT['0'] = subd.Cam('0')
    with mock.patch.object(subd.CV_CAMS_DICT['0'].status_pub, 'publish'
        ) as mock_fail_pub:
        pub_cam_loop_opencv(0, high_speed=False)
        mock_fail_pub.assert_called_once_with('failed')
    subd.CV_CAMS_DICT = {}
