"""Test the sigmaclipping"""
data = np.ones(100)
if not BANE.sigmaclip(data, 3, 4, reps=4)[0] == 1.0:
    raise AssertionError()
data[13] = np.nan
if not BANE.sigmaclip(data, 3, 4, reps=4)[0] == 1.0:
    raise AssertionError()
if not np.isnan(BANE.sigmaclip([], 0, 3)[0]):
    raise AssertionError()
