"""Test that adding a single year constraint works"""
expected_substring = (
    "WHERE game.home_score != game.away_score AND game.finished = TRUE AND play.pos_team != 'UNK' AND (play.time).phase not in ('Pregame', 'Half', 'Final') AND game.season_year = 2013"
    )
assert expected_substring in utils._make_nfldb_query_string(season_years=[2013]
    )
