for name, estimator in ((lgb.sklearn.LGBMClassifier.__name__, lgb.sklearn.
    LGBMClassifier), (lgb.sklearn.LGBMRegressor.__name__, lgb.sklearn.
    LGBMRegressor)):
    check_parameters_default_constructible(name, estimator)
    estimator = estimator(min_child_samples=1, min_data_in_bin=1)
    for check in _yield_all_checks(name, estimator):
        check_name = check.func.__name__ if hasattr(check, 'func'
            ) else check.__name__
        if check_name == 'check_estimators_nan_inf':
            continue
        elif check_name == 'check_no_attributes_set_in_init':
            continue
        try:
            check(name, estimator)
        except SkipTest as message:
            warnings.warn(message, SkipTestWarning)
unittest.skipIf(sk_version < '0.19.0', 'scikit-learn version is less than 0.19'
    )
